import React from 'react';

const City = (props) => {

    const cityIconSize = 100;

    return (
        <div className="app__city">
            <h2>{props.city}</h2>
            <p>{props.country}</p>
            <img src={props.icon} alt="icon" id="icon" style={{ width: cityIconSize, height: cityIconSize }}/>
            <p>{props.temperature} &deg;C</p>
            <p>Feels like {props.feels_like} &deg;C</p>
            <p>Humidity {props.humidity} %</p>
            <p>Chance of rain {props.rain} %</p>
            <p>Max temperature {props.max_temperature} &deg;C</p>
            <p>Min temperature {props.min_temperature} &deg;C</p>
            <p>Wind speed {props.wind} Km/h</p>
        </div>
    )
}

export default City;
