        const searchTemp = async () => {
            const tempURL = `https://api.openweathermap.org/data/2.5/weather?lat=${cityLatitude}&lon=${cityLongitude}&appid=${oldKey}&units=metric`;
            const response = await fetch(tempURL);
            const data = await response.json();
            console.log('Data from first useEffect:  ', data); 
            if (!data) {
                setWeather([])
            } else {
                setWeather(data);
            }
            


