                    <h1 className="degree">{weather.main.temp}&deg;C</h1>
                    <h2 id="city">{clickedCityData.city}</h2>
                    <button id="change">Change City</button>

                    <div className="info">

                        <div>
                            <h3>Wind</h3>
                            <div id="windValue" className="value"><p>{}</p><span>Km/h</span></div>
                        </div>
                        <div>
                            <h3>Feels Like</h3>
                            <div id="feelsLikeValue" className="value"><p>{}&deg;C</p><span></span></div>
                        </div>
                        <div>
                            <h3>Humidity</h3>
                            <div id="humidityValue" className="value"><p>{}</p><span>%</span></div>
                        </div>

                    </div>

                </div>
        </div>



    ) : (
    <form >
            <input onChange={debouncedOnChange} type="text" placeholder="Search City..." />

            {geoLocation.length > 0 ? (
                <ul onClick={handleClick}>
                    {geoLocation.map(({name, lat, lon, country}, index) => (
                        <li className="app__list-item"
                        key={index}
                        data-lat={lat}
                        data-lon={lon}
                        data-city={name}
                        data-country={country}
                        >
                            {name} <span>{country}</span>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>Loading...</p>
            )
            
            }
        </form>

    )}






</div>

    )

     

}


