import React, { useState, useEffect } from 'react';
import debounce from 'lodash.debounce';

import './searchCity.css';


const SearchCity = () => {
//    const Oldkey = "1d57fcba75368cc323dc71241f670c5d";
   const key = "d1befbde65e241b0a7d75223242702"

   const [cityNameInput, setCityNameInput] = useState('');
   const [storedWeather, setStoredWeather] = useState([]);

   /*Here I get the city name from user input */
   const userInput = (ev) => {
       setCityNameInput(ev?.target?.value);
   }

   //Here i make the execution of userInput to wait 6 sec. until run

   const debouncedOnChange = debounce(userInput, 600);
   console.log(`Test 1 City Name: ${cityNameInput}`);
   console.log(`Test 1.2 City Name: ${cityNameInput}`);
//    const OLDcityURL = `http://api.openweathermap.org/geo/1.0/direct?q=${cityNameInput}&limit=5&appid=${key}`;
   const apiURL = `http://api.weatherapi.com/v1/current.json?key=${key}&q=${cityNameInput}&aqi=no`;
   
   let isFirstRender = useRef(true);

   useEffect(() => {
       if (isFirstRender.current) {
           isFirstRender.current = false;
           return;
         }

    const fetchData = async () => {

        if (cityNameInput && cityNameInput.trim() !== "") {
            const result = await fetch(apiURL);
            result.json().then(json => {
                const temperature = json.current.temp_c;
                setStoredWeather(temperature);
            })
        }
        isInitialRender = false;

    };
    fetchData();
    
   }, [cityNameInput]);

   useEffect(() => {
    console.log(`Test 3 City Temp celsius: ${storedWeather}`);
   }, [storedWeather]);



    return (
    <div className="app__main-container" >

    <div  className="app__form">
        <form>
            <input onKeyUp={debouncedOnChange} type="text" placeholder="Search City..." />
            <ul>
            
            </ul>

        </form>
    </div>

    <div className="app__show-wheather ">

        <div id="weather" className="flex__center">
            <img src="" alt="icon" id="icon" />
            <h1 className="degree">&deg;C</h1>
            <h2 id="city"></h2>
            <button id="change">Change City</button>

            <div className="info">

                <div>
                    <h3>Wind</h3>
                    <div id="windValue" className="value"><p></p><span>Km/h</span></div>
                </div>
                <div>
                    <h3>Feels Like</h3>
                    <div id="feelsLikeValue" className="value"><p>&deg;C</p><span></span></div>
                </div>
                <div>
                    <h3>Humidity</h3>
                    <div id="humidityValue" className="value"><p></p><span>%</span></div>
                </div>

            </div>

        </div>


    </div>
        
    </div>
    )

};

export default SearchCity;
