import React, {useState, useEffect} from 'react';

import './weatherScreen.css'
const WeatherScreen = () => {
    const oldKey = "1d57fcba75368cc323dc71241f670c5d";

    const [weather, setWeather] = useState([]);
    const cityName = 'London';


    useEffect(() => {
        const search = async () => {
            const URL = `http://api.openweathermap.org/geo/1.0/direct?q=${cityName},&limit=5&appid=${oldKey}`;
            const response = await fetch(URL);
            const data = await response.json();
            console.log(data);
            setWeather(data);
        }
        search();
    }, [cityName]);

    console.log(`Here I have the weather from weather variable: ${weather}`);

   const handleClick = (e) => {
       const li = e.target;
       const {lat, lon, name} = li.dataset;
       if (!name) {
        return;
       }
       console.log(`Li ${lon} ${name}`);
      
   }




return (
<div >
    <ul >
        {weather.map(({name, lat, lon, country}, index) => (
            <li className="app__list-item"
            onClick={handleClick}
            key={index}
            data-lat={lat}
            data-lon={lon}
            data-name={name}
            >
            
                <p>{name}</p> <span>{country}</span>
            </li>
        ))}
    </ul>

</div>

    )

     

}


export default WeatherScreen;








