import React, { useState, useEffect } from 'react';
import debounce from 'lodash.debounce';

import './searchCity.css';


const SearchCity = () => {
    const key = "1d57fcba75368cc323dc71241f670c5d";
    
    const [userInput, setUserInput] = useState("");
    const [ulValue, setUlValue] = useState([]);
    const [weather, setWeather] = useState([]);
    const updateUserInput = e => setUserInput(e?.target?.value);
    const debouncedOnChange = debounce(updateUserInput, 600);
    
        const showWeather = (lat, lon, name) => { }
         
            useEffect(() => {
                fetch(`https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=${key}&units=metric`)
            .then(response => response.json())
            .then(data => {
                if (Array.isArray(data)) {
                    setWeather(data)
                    console.log(data);
                } else {
                    setWeather([]);
                }
                })
            .catch(err => console.log(err))

        }, [], lat , lon, key);
       
    
        const handleClick = (ev) => {
            const li = ev.target;
            const {lat, lon, name} = li.dataset;
            if (!lat) {
                return;
            }
            showWeather(lat, lon, name);
        }



    useEffect(() => {
         fetch(`http://api.openweathermap.org/geo/1.0/direct?q=${userInput}&limit=5&appid=${key}`)
        .then(response => response.json())
        .then(data => {
            if (Array.isArray(data)) {
              setUlValue(data);
            } else {
              setUlValue([]);
            }
          })
        .catch(err => console.log(err))

    }, [userInput], key);

    const mapItems = () => (
        ulValue.map(({name, lat, lon, country}, index) => (
            <li 
            onClick={handleClick}
            data-lat={lat} 
            data-lon={lon} 
            data-name={name} 
            key={index}>
            {name} <span>{country}</span></li>
        ))
    )

    return (
    <div>
        <form>
            <input onKeyUp={debouncedOnChange} type="text" placeholder="Search City..." />
            <ul>
            {mapItems()}
            </ul>
        </form>
    </div>
    )

};

export default SearchCity;
