import React, {useState, useEffect} from 'react';
import debounce from 'lodash.debounce';



import './weatherScreen.css'
const WeatherScreen = () => {
    const [showWeather, setShowWeather] = useState(false);
    const [icon, setIcon] = useState('');
    const image = '/images/sunny.png'

    const [weather, setWeather] = useState({
        temperature: 0,
        humidity: 0,
        feels_like: 0,
        wind_speed: 0,
        rain: 0,
        max_temperature: 0,
        min_temperature: 0
    });
    const [geoLocation, setGeoLocation] = useState([]);
    const [clickedCityData, setClickedCityData] = useState({
        lat: 0,
        lon: 0,
        city: '',
        country: ''
    });

    //Getting the user input:
    const [userInput, setUserInput] = useState("");
    
    const updateUserInput = (e) => {
        setUserInput(e?.target?.value); 
    }

    //Setting the waiting time for user input:
    const debouncedOnChange = debounce(updateUserInput, 600);

    useEffect(() => {
        const searchTemp = async () => {
            
        const tempURL = `https://api.open-meteo.com/v1/forecast?latitude=${clickedCityData.lat}&longitude=${clickedCityData.lon}&current=temperature_2m,relative_humidity_2m,apparent_temperature,is_day,precipitation,rain,wind_speed_10m,wind_direction_10m&hourly=temperature_2m,relative_humidity_2m,apparent_temperature,precipitation_probability,precipitation,rain,snowfall,visibility,wind_speed_10m,wind_speed_80m,wind_speed_120m&daily=temperature_2m_max,temperature_2m_min,sunrise,sunset,daylight_duration,sunshine_duration&daily=weather_code&timezone=GMT`;

        // const tempURL_2 = `https://api.open-meteo.com/v1/forecast?latitude=${clickedCityData.lat}&longitude=${clickedCityData.lon}&current=temperature_2m,relative_humidity_2m,apparent_temperature,is_day,precipitation,rain,showers,snowfall,weather_code,cloud_cover,
        // pressure_msl,surface_pressure,wind_speed_10m,wind_direction_10m,wind_gusts_10m&hourly=temperature_2m,relative_humidity_2m,dew_point_2m,apparent_temperature,precipitation,rain,showers,snowfall,snow_depth,weather_code,pressure_msl,surface_pressure,
        // cloud_cover,cloud_cover_low,cloud_cover_mid,cloud_cover_high,evapotranspiration,et0_fao_evapotranspiration,vapour_pressure_deficit,wind_speed_10m,wind_speed_80m,wind_speed_120m,wind_speed_180m,wind_direction_10m,wind_direction_80m,wind_direction_120m,
        // wind_direction_180m,wind_gusts_10m,temperature_80m,temperature_120m,temperature_180m,soil_temperature_0cm,soil_temperature_6cm,soil_temperature_18cm,soil_temperature_54cm,soil_moisture_0_to_1cm,soil_moisture_1_to_3cm,soil_moisture_3_to_9cm,soil_moisture_9_to_27cm,
        // soil_moisture_27_to_81cm,is_day,lightning_potential,updraft,cape,freezing_level_height,snowfall_height,sunshine_duration&daily=weather_code,temperature_2m_max,temperature_2m_min,apparent_temperature_max,apparent_temperature_min,sunrise,sunset,daylight_duration,
        // sunshine_duration,precipitation_sum,rain_sum,showers_sum,snowfall_sum,precipitation_hours,precipitation_probability_max,wind_speed_10m_max,wind_gusts_10m_max,wind_direction_10m_dominant,shortwave_radiation_sum,et0_fao_evapotranspiration&minutely_15=precipitation,rain,
        // snowfall,snowfall_height,freezing_level_height,cape,lightning_potential,is_day,sunshine_duration,shortwave_radiation,direct_radiation,diffuse_radiation,direct_normal_irradiance,global_tilted_irradiance,shortwave_radiation_instant,direct_radiation_instant,diffuse_radiation_instant,
        // direct_normal_irradiance_instant,global_tilted_irradiance_instant,terrestrial_radiation_instant&timezone=Europe%2FLondon`
        
            const results = await fetch(tempURL);
            const data = await results.json();
            console.log('Data from first useEffect:  ', data); 

            const temperature = Math.round(data.current.temperature_2m);
            const humidity = Math.round(data.current.relative_humidity_2m);
            const feels_like = Math.round(data.current.apparent_temperature);
            const wind_speed = data.current.wind_speed_10m;
            const rain = data.current.rain;
            const max_temperature = Math.floor(data.daily.temperature_2m_max[0]);
            const min_temperature = Math.floor(Math.min(...data.daily.temperature_2m_min));

            const weatherType = data.daily.weather_code;

            console.log(weatherType[0]);


        setWeather({
            temperature: temperature,
            humidity: humidity,
            feels_like: feels_like,
            wind_speed: wind_speed,
            rain: rain,
            max_temperature: max_temperature,
            min_temperature: min_temperature
        });
    };
    searchTemp();
    
}, [clickedCityData]);

//Here I take the geoLocation data.

    useEffect(() => {
        const search = async () => {
            const URL = `https://geocoding-api.open-meteo.com/v1/search?name=${userInput}&count=5&language=en&format=json`
            const results = await fetch(URL);
            const data = await results.json();
            console.log('Data from second useEffect: ', data.results);

            setGeoLocation(data.results);                                         
        }
        search();
    }, [userInput]);

//Here i get the clicked city geolocation info

   const handleClick = (e) => {
       const li = e.target;
       const data = li.dataset;  // Access the dataset of the clicked element
       console.log('dataset: ',li.dataset)

       setClickedCityData({lat: data.lat, lon: data.lon, city: data.city, country: data.country});
       setShowWeather(true);

   }

   function handleSpanClick(event) {
    event.stopPropagation();
    const li = event.target.parentNode;
    const data = li.dataset;
    setClickedCityData({lat: data.lat, lon: data.lon, city: data.city, country: data.country});
    setShowWeather(true);
    // Perform any necessary actions with the clicked span data
    
  }

    const buttonClick = () => {
        setShowWeather(false);
    }

return (

        <div className="app__container">

            {showWeather ? (
                <div className="app__show-weather ">

                        <div id="weather" className="flex__center">
                            <img src={image} alt="icon" id="icon" />
                            <h1 className="app__degree">{weather.temperature}&deg;C</h1>
                            <h2 id="city">{clickedCityData.city} {clickedCityData.country}</h2>
                            <button className="btn__change-city btn" onClick={buttonClick} id="change">Change City</button>

                            <div className="info">

                                <div className="value">
                                    <h3>Wind</h3>
                                    <div id="windValue" ><p>{weather.wind_speed} Km/h</p></div>
                                </div>
                                <div className="value">
                                    <h3>Feels Like</h3>
                                    <div id="feelsLikeValue" ><p>{weather.feels_like}&deg;C</p></div>
                                </div>
                                <div className="value">
                                    <h3>Humidity</h3>
                                    <div id="humidityValue" ><p>{weather.humidity}%</p></div>
                                </div>
                                <div className="value">
                                    <h3>Chance of Rain</h3>
                                    <div id="rainValue" ><p>{weather.rain}%</p></div>
                                </div>
                                <div className="value">
                                    <h3>MaxTemperature</h3>
                                    <div id="max-temp-Value" ><p>{weather.max_temperature}&deg;C</p></div> 
                                </div>
                                <div className="value">
                                    <h3>MinTemperature</h3>
                                    <div id="min-temp-Value" ><p>{weather.min_temperature}&deg;C</p></div>
                                </div>

                            </div>

                        </div>
                </div>



            ) : (
                <div  className="app__form-container">
                    <form >
                        <input className="input__field" onChange={debouncedOnChange} type="text" placeholder="Search City..." />

                        {geoLocation ? (
                            <ul onClick={handleClick}>
                                {geoLocation.map(({name, latitude, longitude, country}, index) => (
                                    <li className="app__list-item"
                                    key={index}
                                    data-lat={latitude}
                                    data-lon={longitude}
                                    data-city={name}
                                    data-country={country}
                                    >
                                    {name} <span onClick={handleSpanClick}>{country}</span>
                                    
                                    </li>
                                ))}
                            </ul>
                        ) : (
                            <p>Waiting...</p>
                        )
                        
                        }
                    </form>
                </div>

            )}

        </div>

       )
}


export default WeatherScreen;
