import React, { useState, useEffect} from 'react';
import debounce from 'lodash.debounce';

import { useRef } from 'react';

import './searchCity.css';


const SearchCity = () => {
//    const Oldkey = "1d57fcba75368cc323dc71241f670c5d";
   const key = "d1befbde65e241b0a7d75223242702"

   const [cityNameInput, setCityNameInput] = useState('');
   const [storedWeather, setStoredWeather] = useState({
    temperature: 0,
    wind: 0,
    humidity: 0,
    feelslike_c: 0,
    weatherType: '',
    icon: '',
    country: '',
    localTime: 0,
    lastUpdated: ''
   });

   const [liDropInfo, setLiDropInfo] = useState([{}]);


   /*Here I get the city name from user input */
   const userInput = (ev) => {
       setCityNameInput(ev?.target?.value);
   }

   //Here i make the execution of userInput to wait 6 sec. until run

   const debouncedOnChange = debounce(userInput, 600);

//    const OLDcityURL = `http://api.openweathermap.org/geo/1.0/direct?q=${cityNameInput}&limit=5&appid=${key}`;
   const apiURL = `http://api.weatherapi.com/v1/current.json?key=${key}&q=${cityNameInput}&aqi=no`;
   
  

   useEffect(() => {

    const fetchData = async () => {

        if (cityNameInput && cityNameInput.trim() !== "") {
            const result = await fetch(apiURL);
            result.json().then(json => {
                const temperature = json.current.temp_c;
                const wind = json.current.wind_kph;
                const humidity = json.current.humidity;
                const feelslike_c = json.current.feelslike_c;
                const weatherType = json.current.condition.text;
                const icon = json.current.condition.icon;
                const localTime = json.location.localtime;
                const lastUpdated = json.current.last_updated;
                const country = json.location.country;
                setStoredWeather({temperature, wind, humidity, feelslike_c, weatherType, icon, country, localTime, lastUpdated});
                
                const lat = json.location.lat;
                const lon = json.location.lon;
                const cityName = json.location.name;
                // setLiDropInfo([{lat, lon, cityName, country}]);

            })
        }

    };
    fetchData();
    
   }, [cityNameInput]);
 
//    const liURL = `http://api.weatherapi.com/v1key=${key}&q=${liDropInfo.lat}&q=${liDropInfo.lon}`

//    useEffect(() => {
//     const fetchData = async () => {
//         try {
//             const response = await fetch(liURL);
//             if (!response.ok) {
//               throw new Error('Network response was not ok');
//             }
//             const data = await response.json();
//             setLiDropInfo([data.location]);
//           } catch (error) {
//             console.log(error);
//           }
//         };
//     fetchData();
//    }, []);

   //////

   useEffect(() => {
    const fetchData = async () => {
            const response = await fetch(liURL);
            console.log(response);
          }

    fetchData();
   }, []);




// HERE I START THE SEARCH CITY AND LIST DROP DOWN
const handleClick = (e) => {
    const li = e.target;
    console.log('working click');
}

const mapItems = () => (
    liDropInfo.map(({ cityName, lat, lon, country }, index) => (
       <li
       data-lat={lat}
       data-lon={lon}
       data-name={cityName}
       key={index}
       >

       {cityName} <span>{country}</span>

       </li>
    ))
)



    return (
    <div className="app__main-container" >

    <div onClick={handleClick} className="app__form">
        <form>
            <input onKeyUp={debouncedOnChange} type="text" placeholder="Search City..." />
            <ul>
              {mapItems()}
            </ul>

        </form>
    </div>

    <div className="app__show-wheather ">

        <div id="weather" className="flex__center">
            <img src={storedWeather.icon} alt="icon" id="icon" />
            <h1 className="degree">{storedWeather.temperature}&deg;C</h1>
            <h3 >{storedWeather.weatherType}</h3>
            <h2 id="city">{cityNameInput}</h2>
            <button id="change">Change City</button>

            <div className="info">

                <div>
                    <h3>Wind</h3>
                    <div id="windValue" className="value"><p>{storedWeather.wind}</p><span>Km/h</span></div>
                </div>
                <div>
                    <h3>Feels Like</h3>
                    <div id="feelsLikeValue" className="value"><p>{storedWeather.feelslike_c}&deg;C</p><span></span></div>
                </div>
                <div>
                    <h3>Humidity</h3>
                    <div id="humidityValue" className="value"><p>{storedWeather.humidity}</p><span>%</span></div>
                </div>

            </div>

        </div>


    </div>
        
    </div>
    )

};

export default SearchCity;
