import React, {useState, useEffect} from 'react';

import './weatherScreen.css'
const WeatherScreen = () => {
    const oldKey = "1d57fcba75368cc323dc71241f670c5d";

    const [cityLatitude, setCityLatitude] = useState([]);
    const [cityLongitude, setCityLongitude] = useState([]);

    const [cityName, setCityName] = useState('');
    const [weather, setWeather] = useState([]);
    const [geoLocation, setGeoLocation] = useState([]);
    const [clickedCityData, setClickedCityData] = useState([]);

    useEffect(() => {
        const searchTemp = async () => {
            const tempURL = `https://api.openweathermap.org/data/2.5/weather?lat=${cityLatitude}&lon=${cityLongitude}&appid=${oldKey}`;
            const response = await fetch(tempURL);
            const data = await response.json();
            console.log('city weather from data: ', data);

            setWeather(data);


        }
        searchTemp();
    
    }, [cityLatitude],[cityLongitude]);


//From here I satrt the process of getting geolocation of the city     

//Here I take the geoLocation data.
    useEffect(() => {
        const search = async () => {
            const URL = `http://api.openweathermap.org/geo/1.0/direct?q=${cityName},&limit=5&appid=${oldKey}`;
            const response = await fetch(URL);
            const data = await response.json();
           
            data.map(({lat, lon}) => {
               
                setCityLatitude(lat);
                setCityLongitude(lon);
                
                
            })
            
            setGeoLocation(data);                                        
        }
        search();
    }, [cityName]);

    // console.log('geolocation hook:', geoLocation.lat);
//------------------------------------>


//Here i get the clicked city geolocation info
   const handleClick = (e) => {
       const li = e.target;
       const data = li.dataset;  // Access the dataset of the clicked element
    //    setCityName(data.city);
    //    setClickedCityData(data);

    //    console.log('clicked element:', li);
       console.log('Clicked element city name:', data.city);
    //    console.log('Clicked element city country:', data.country);
    //    console.log('Clicked element city latitude:', data.lat);
    //    console.log('Clicked element city longitude:', data.lon);
   }
//---------------------------------------------->




return (
<div onClick={handleClick}>
    <ul >
        {geoLocation.map(({name, lat, lon, country}, index) => (
            <li className="app__list-item"
            key={index}
            data-lat={lat}
            data-lon={lon}
            data-city={name}
            data-country={country}
            >
                {name} <span>{country}</span>
            </li>
        ))}
    </ul>
    {clickedCityData && (
        <div>
        <p>{clickedCityData.name}</p>
        </div>
    )}

</div>

    )

     

}


export default WeatherScreen;








