import React, { useState, useEffect } from 'react';
import debounce from 'lodash.debounce';

import './searchCity.css';


const SearchCity = () => {
   const key = "1d57fcba75368cc323dc71241f670c5d";

    const [infoToExport, setInfoToExport] = useState([{
        temp: 0,
        feelsLike: 0,
        humidity: 0,
        wind: 0,
        icon: ''
    }]);

    
    const [userInput, setUserInput] = useState("");
    const [ulValue, setUlValue] = useState([]);
    const updateUserInput = e => setUserInput(e?.target?.value);
    const debouncedOnChange = debounce(updateUserInput, 600);

    
/*Here I get the weather information */

    const [weatherData, setWeather] = useState({});
    const [info, setInfo] = useState({
        cityLat: 0,
        cityLon: 0,
        cityName : '',
        
    })

    console.log(info.cityLat);
    
    
    useEffect(() => {
        if (!info.cityLat && !info.cityLon) {
            return;
        }
        fetch(`https://api.openweathermap.org/data/2.5/weather?lat=${info.cityLat}&lon=${info.cityLon}&appid=${key}&units=metric`)
        .then(response => response.json())
        .then(data => setWeather(data))
        .catch(err => console.log(err))
    }, []);
    
    
    const handleClick = (ev) => {
        const li = ev.target;
        const {lat, lon, name} = li.dataset;
        
        if (!lat) {
            return;
        }
        console.log(`Test ${lat}`);
        setInfo(({
            cityLat: lat,
            cityLon: lon,
            cityName: name
        }));
          
        
        const temp = Math.round(weatherData.main.temp);
        const feelsLike = Math.round(weatherData.main.feels_like);
        const humidity = Math.round(weatherData.main.humidity);
        const wind = Math.round(weatherData.wind.speed);
        const icon = weatherData.weather[0].icon;
        setInfoToExport({ temp, feelsLike, humidity, wind, icon });
        }

/* Trough here */

/*Here I get the City name and can click on it to get the onfo about the weather */
    useEffect(() => {
         fetch(`http://api.openweathermap.org/geo/1.0/direct?q=${userInput}&limit=5&appid=${key}`)
        .then(response => response.json())
        .then(data => {
            if (Array.isArray(data)) {
              setUlValue(data);
              
            } else {
              setUlValue([]);
            }
          })
        .catch(err => console.log(err))

    }, [userInput]);

    const mapItems = () => (
        ulValue.map(({name, lat, lon, country}, index) => (
            <li 
            
            data-lat={lat} 
            data-lon={lon} 
            data-name={name} 
            key={index}>
            {name} <span>{country}</span></li>
        ))
    )

/* Trough here */


    return (
    <div className="app__main-container" >

    <div onClick={handleClick} className="app__form">
        <form>
            <input onKeyUp={debouncedOnChange} type="text" placeholder="Search City..." />
            <ul>
            {mapItems()}
            </ul>

        </form>
    </div>

    <div className="app__show-wheather ">

        <div id="weather" className="flex__center">
            <img src={`https://openweathermap.org/img/wn/${infoToExport.icon}@4x.png`} alt="icon" id="icon" />
            <h1 className="degree">{infoToExport.temp}&deg;C</h1>
            <h2 id="city">{info.cityName}</h2>
            <button id="change">Change City</button>

            <div className="info">

                <div>
                    <h3>Wind</h3>
                    <div id="windValue" className="value"><p>{infoToExport.wind}</p><span>Km/h</span></div>
                </div>
                <div>
                    <h3>Feels Like</h3>
                    <div id="feelsLikeValue" className="value"><p>{infoToExport.feelsLike}&deg;C</p><span></span></div>
                </div>
                <div>
                    <h3>Humidity</h3>
                    <div id="humidityValue" className="value"><p>{infoToExport.humidity}</p><span>%</span></div>
                </div>

            </div>

        </div>


    </div>
        
    </div>
    )

};

export default SearchCity;
