const SearchCity = () => {
    const key = "1d57fcba75368cc323dc71241f670c5d";
    
    const [userInput, setUserInput] = useState("");
    const [ulValue, setUlValue] = useState([]);
    const updateUserInput = e => setUserInput(e?.target?.value);
    const debouncedOnChange = debounce(updateUserInput, 600);



    
    useEffect(() => {
        const fetchData = async () => {
            
            const URL = `http://api.openweathermap.org/geo/1.0/direct?q=${userInput}&limit=5&appid=${key}`;
            const response = await fetch(URL);
            const data = await response.json();
            console.log(data)

           const mappedItems = data.map((item) => (
            <li key={item.name}>{item.name}</li>
             )
           )
          setUlValue(mappedItems);

        }
        fetchData();
    }, [userInput]);


    return (
    <div>
        <form>
            <input onKeyUp={debouncedOnChange} type="text" placeholder="Search City..." />
            <ul>
            {ulValue}
            </ul>
        </form>
    </div>
    )

};




export default SearchCity;
