import React, {useState, useEffect} from 'react';
import debounce from 'lodash.debounce';

import './weatherScreen.css'
import { max } from 'lodash';
const WeatherScreen = () => {
    const [showWeather, setShowWeather] = useState(false);

    const [weather, setWeather] = useState({
        temperature: 0,
        humidity: 0,
        feels_like: 0,
        wind_speed: 0,
        rain: 0,
        max_temperature: 0,
        min_temperature: 0
    });
    const [geoLocation, setGeoLocation] = useState([]);
    const [clickedCityData, setClickedCityData] = useState({
        lat: 0,
        lon: 0,
        city: '',
        country: ''
    });

    //Getting the user input:
    const [userInput, setUserInput] = useState("");
    
    const updateUserInput = (e) => {
        setUserInput(e?.target?.value); 
    }

    //Setting the waiting time for user input:
    const debouncedOnChange = debounce(updateUserInput, 600);

    useEffect(() => {
        const searchTemp = async () => {
            
        const tempURL = `https://api.open-meteo.com/v1/forecast?latitude=${clickedCityData.lat}&longitude=${clickedCityData.lon}&current=temperature_2m,relative_humidity_2m,apparent_temperature,is_day,precipitation,rain,wind_speed_10m,wind_direction_10m&hourly=temperature_2m,relative_humidity_2m,apparent_temperature,precipitation_probability,precipitation,rain,snowfall,visibility,wind_speed_10m,wind_speed_80m,wind_speed_120m&daily=temperature_2m_max,temperature_2m_min,sunrise,sunset,daylight_duration,sunshine_duration&timezone=GMT`;
        
            const results = await fetch(tempURL);
            const data = await results.json();
            console.log('Data from first useEffect:  ', data); 

            const temperature = Math.round(data.current.temperature_2m);
            const humidity = Math.round(data.current.relative_humidity_2m);
            const feels_like = Math.round(data.current.apparent_temperature);
            const wind_speed = data.current.wind_speed_10m;
            const rain = data.current.rain;
            const max_temperature = Math.floor(data.daily.temperature_2m_max[0]);
            const min_temperature = Math.floor(Math.min(...data.daily.temperature_2m_min)) ;


        setWeather({
            temperature: temperature,
            humidity: humidity,
            feels_like: feels_like,
            wind_speed: wind_speed,
            rain: rain,
            max_temperature: max_temperature,
            min_temperature: min_temperature
        });
    };
    searchTemp();
    
}, [clickedCityData]);

//Here I take the geoLocation data.

    useEffect(() => {
        const search = async () => {
            const URL = `https://geocoding-api.open-meteo.com/v1/search?name=${userInput}&count=5&language=en&format=json`
            const results = await fetch(URL);
            const data = await results.json();
            console.log('Data from second useEffect: ', data.results);

            setGeoLocation(data.results);                                         
        }
        search();
    }, [userInput]);

//Here i get the clicked city geolocation info

   const handleClick = (e) => {
       const li = e.target;
       const data = li.dataset;  // Access the dataset of the clicked element
       console.log('dataset: ',li.dataset)

       setClickedCityData({lat: data.lat, lon: data.lon, city: data.city, country: data.country});
       setShowWeather(true)

   }

    const buttonClick = () => {
        setShowWeather(false);
    }

return (

        <div className="app__container">

            {showWeather ? (
                <div className="app__show-wheather ">

                        <div id="weather" className="flex__center">
                            {/* <img src={`https://openweathermap.org/img/wn/${infoToExport.icon}@4x.png`} alt="icon" id="icon" /> */}
                            <h1 className="degree">{weather.temperature}&deg;C</h1>
                            <h2 id="city">{clickedCityData.city} {clickedCityData.country}</h2>
                            <button onClick={buttonClick} id="change">Change City</button>

                            <div className="info">

                                <div>
                                    <h3>Wind</h3>
                                    <div id="windValue" className="value"><p>{weather.wind_speed}</p><span>Km/h</span></div>
                                </div>
                                <div>
                                    <h3>Feels Like</h3>
                                    <div id="feelsLikeValue" className="value"><p>{weather.feels_like}&deg;C</p><span></span></div>
                                </div>
                                <div>
                                    <h3>Humidity</h3>
                                    <div id="humidityValue" className="value"><p>{weather.humidity}</p><span>%</span></div>
                                </div>
                                <div>
                                    <h3>Chance of Rain</h3>
                                    <div id="rainValue" className="value"><p>{weather.rain}</p><span>%</span></div>
                                </div>
                                <div>
                                    <h3>Min-Max Temperature</h3>
                                    <div id="min-maxValue" className="value">
                                    <p>{weather.min_temperature}</p><span>&deg;C</span>
                                    <p>{weather.max_temperature}</p><span>&deg;C</span>

                                    </div>
                                </div>

                            </div>

                        </div>
                </div>



            ) : (
                <div onClick={handleClick} className="app__form-container">
                    <form >
                        <input className="input__field" onChange={debouncedOnChange} type="text" placeholder="Search City..." />

                        {geoLocation ? (
                            <ul >
                                {geoLocation.map(({name, latitude, longitude, country}, index) => (
                                    <li className="app__list-item"
                                    key={index}
                                    data-lat={latitude}
                                    data-lon={longitude}
                                    data-city={name}
                                    data-country={country}
                                    >
                                    {name} <span>{country}</span>
                                    </li>
                                ))}
                            </ul>
                        ) : (
                            <p>Waiting...</p>
                        )
                        
                        }
                    </form>
                </div>

            )}

        </div>

       )
}


export default WeatherScreen;
