import React, { useState, useEffect } from 'react';
import debounce from 'lodash.debounce';

import './searchCity.css';



const SearchCity = () => {
    const key = "1d57fcba75368cc323dc71241f670c5d";
    
    const [userInput, setUserInput] = useState("");
    
    const updateUserInput = e => setUserInput(e?.target?.value);

    const DataFetcher = () => {
        const [data, setData] = useState(null);
        const [loading, setLoading] = useState(true);
        const [error, setError] = useState(null);

        const debouncedOnChange = debounce(updateUserInput, 600);
    
        useEffect(() => {
            async function search() {
                try {
                    const response = await search(`http://api.openweathermap.org/geo/1.0/direct?q=${userInput}&limit=5&appid={key}`);
                    const data = await response.json();
                    setData(data);
                    console.log(data);
                    setLoading(false);
                } catch (error) {
                    setError(error);
                    setLoading(false);
                }
            }
    
            search();
        }, []);
    }

    

    return (
    <div>
        <form>
            <input onChange={debouncedOnChange} type="text" placeholder="Search City..." />
            <ul>
                <li>Test 1 <span>Uk</span></li>
                <li>Test 2</li>
                <li>Test 3</li>
                <li>Test 4</li>
                <li>Test 5</li>
            </ul>
        </form>
    </div>
    )

};




export default SearchCity;
